<script>
  export let src = "";
  export let alt = "";
  export let className = "";
  export let style = "";
  export let rest = {}; // catch-all additional props

  let didError = false;

  const ERROR_IMG_SRC =
    'data:image/svg+xml;base64,';

  function handleError() {
    didError = true;
  }
</script>

{#if didError}
  <div class={`inline-block bg-gray-100 text-center align-middle ${className}`} style={style}>
    <div class="flex items-center justify-center w-full h-full">
      <img src={ERROR_IMG_SRC} alt="Error loading image" {...rest} data-original-url={src} />
    </div>
  </div>
{:else}
  <img src={src} alt={alt} class={className} style={style} on:error={handleError} {...rest} />
{/if}
